import defaultTheme from 'simple-mind-map/src/themes/default';
import merge from 'deepmerge';

/** 
 * @Author: 王林 
 * @Date: 2021-04-11 15:22:18 
 * @Desc: 脑图经典
 */
export default merge(defaultTheme, {
    // 连线的颜色
    lineColor: '#fff',
    // 连线的粗细
    lineWidth: 3,
    // 概要连线的粗细
    generalizationLineWidth: 3,
    // 概要连线的颜色
    generalizationLineColor: '#fff',
    // 背景颜色
    backgroundColor: 'rgb(58, 65, 68)',
    // 背景图片
    backgroundImage: 'data:image/png;base64,',
    // 背景重复
    backgroundRepeat: 'repeat',
    // 根节点样式
    root: {
        fillColor: 'rgb(233, 223, 152)',
        color: '#333',
        fontSize: 24,
        borderRadius: 21,
        active: {
            fillColor: 'rgb(254, 219, 0)',
            borderColor: 'transparent'
        }
    },
    // 二级节点样式
    second: {
        fillColor: 'rgb(164, 197, 192)',
        borderColor: 'transparent',
        color: '#333',
        fontSize: 16,
        borderRadius: 10,
        active: {
            fillColor: 'rgb(254, 219, 0)',
            borderColor: 'transparent'
        }
    },
    // 三级及以下节点样式
    node: {
        fontSize: 12,
        color: '#fff',
        fontWeight: 'bold',
        active: {
            fillColor: 'rgb(254, 219, 0)',
            borderColor: 'transparent'
        }
    },
    // 概要节点样式
    generalization: {
        fillColor: '#fff',
        borderColor: 'transparent',
        color: '#333',
        active: {
            fillColor: 'rgb(254, 219, 0)',
            borderColor: 'transparent'
        }
    }
})
