        }
    },
    // 概要节点样式
    generalization: {
        fillColor: 'rgb(56, 123, 233)',
        borderColor: 'rgb(56, 123, 233)',
        color: '#fff',
        borderWidth: 0,
        active: {
            borderColor: 'rgb(169, 218, 218)'
        }
    }
})
