import request from "@/utils/request";
export default {
  name: "Header",

  data() {
    const checkID = (rule, value, callback) => {
      if (!value) {
        return callback(new Error('学号不能为空'));
      }

      let regPos = /[0-9]/;

      if (!regPos.test(value)) {
        callback(new Error('请输入学号'));
      } else {
        if (value.length !== 12) {
          callback(new Error('学号必须为12位'));
        } else {
          callback();
        }
      }
    };

    const validatePass = (rule, value, callback) => {
      if (value === '') {
        callback(new Error('请输入密码'));
      } else {
        callback();
      }
    };

    return {
      activeIndex: 'this.$router.path',
      isLogin: false,
      User: {
        name: '张三',
        id: '202034340099',
        password: ''
      },
      loginDialog: false,
      rules: {
        id: [{
          validator: checkID,
          trigger: 'blur'
        }],
        password: [{
          validator: validatePass,
          trigger: 'blur'
        }]
      }
    };
  },

  methods: {
    login() {
      request.post("/login", this.User).then(res => {
        if (res.code === 0) {
          this.isLogin = true;
          this.$message({
            type: "success",
            message: "登陆成功"
          });
        } else {
          this.$message({
            type: "error",
            message: "登陆失败"
          });
        }
      });
    }

  }
};
