import MindMap from 'simple-mind-map';
import { getData, storeData, storeConfig } from '@/api';
export default {
  name: 'HelloWorld',

  data() {
    return {
      mindmapdata: null
    };
  },

  mounted() {
    this.getData();
    this.init();
  },

  methods: {
    getData() {
      let storeData = getData();
      this.mindMapData = storeData;
    },

    init() {
      let {
        root,
        layout,
        theme,
        view
      } = this.mindMapData;
      this.mindMap = new MindMap({
        el: document.getElementById('mindMapContainer'),
        data: root,
        layout: layout,
        theme: 'classic3',
        themeConfig: theme.config,
        viewData: view
      });
      this.mindMap.on('mousedown', (...args) => {
        console.log("鼠标按下");
        this.mindMap.setTheme('diyColor');
        console.log("this.mindMap", this.mindMap);
      });
      this.mindMap.on('node_mousedown', data => {
        console.log("nodemousedown  data", data); //console.log("nodemousedown datae",e)
      });
      console.log("this.mindMap.theme", this.mindMap.theme); // this.mindMap.render()
    }

  }
};
